namespace RK\HelperModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use RK\HelperModule\Entity\ImageEntity;

/**
 * Event base class for filtering image processing.
 */
class AbstractFilterImageEvent extends Event
{
    /**
     * @var ImageEntity Reference to treated entity instance.
     */
    protected $image;

    public function __construct(ImageEntity $image)
    {
        $this->image = $image;
    }

    public function getImage()
    {
        return $this->image;
    }
}
